    def on_pushbutton_clicked(self):
        theme = self.lineEdit_1.text()
        subject = self.lineEdit_2.text()
        if not theme or not subject:
            self.textEdit.setText("请填写论文主要方向和主题！")
            return

        # 创建论文写作系统实例
        spws = StreamingPaperWritingSystem(ptr=self)
        spws.theme = theme
        spws.subject = subject

        # 运行论文写作流程
        spws.run()

    # 在 SPWS 类中添加一个方法来处理带颜色的文本输出
    def append_colored_text(self, text, color_name="black"):
        """向 textEdit 添加带颜色的文本"""
        cursor = self.textEdit.textCursor()
        cursor.movePosition(QtGui.QTextCursor.MoveOperation.End)
        
        # 设置文本格式
        fmt = QtGui.QTextCharFormat()
        if color_name == "red":
            fmt.setForeground(QtGui.QColor("#FF0000"))  # 红色
        elif color_name == "green":
            fmt.setForeground(QtGui.QColor("#00FF00"))  # 绿色
        elif color_name == "yellow":
            fmt.setForeground(QtGui.QColor("#FFD700"))  # 黄色
        elif color_name == "blue":
            fmt.setForeground(QtGui.QColor("#0000FF"))  # 蓝色
        elif color_name == "magenta":
            fmt.setForeground(QtGui.QColor("#FF00FF"))  # 洋红
        elif color_name == "cyan":
            fmt.setForeground(QtGui.QColor("#00FFFF"))  # 青色
        else:
            fmt.setForeground(QtGui.QColor("#000000"))  # 默认黑色
        
        # 应用格式并插入文本
        cursor.insertText(text + "\n", fmt)
        self.textEdit.setTextCursor(cursor)
        self.textEdit.ensureCursorVisible()  # 自动滚动到底部
    
    def append_text(self, text, color="black", newline=True):
        """向 textEdit 添加文本（可选择颜色和是否换行）"""
        cursor = self.textEdit.textCursor()
        cursor.movePosition(QtGui.QTextCursor.MoveOperation.End)
        
        # 设置文本格式
        fmt = QtGui.QTextCharFormat()
        if color == "red":
            fmt.setForeground(QtGui.QColor("#FF0000"))  # 红色
        elif color == "green":
            fmt.setForeground(QtGui.QColor("#00FF00"))  # 绿色
        elif color == "yellow":
            fmt.setForeground(QtGui.QColor("#FFD700"))  # 黄色
        elif color == "blue":
            fmt.setForeground(QtGui.QColor("#0000FF"))  # 蓝色
        elif color == "magenta":
            fmt.setForeground(QtGui.QColor("#FF00FF"))  # 洋红
        elif color == "cyan":
            fmt.setForeground(QtGui.QColor("#00FFFF"))  # 青色
        else:
            fmt.setForeground(QtGui.QColor("#000000"))  # 默认黑色
        
        # 插入文本
        cursor.insertText(text, fmt)
        if newline:
            cursor.insertText("\n")
        
        self.textEdit.setTextCursor(cursor)
        self.textEdit.ensureCursorVisible()

